parameters.jsCode:
const scenes = []

const urlArray = [
  $('Edit Fields').first().json.seed_image,
  ...$input.first().json.data.map(item => item.images[0].url)
]

$('Create scenes').first().json.output.scenes.forEach((item, index) => {
  const start_image = urlArray[index];
  const end_image = index < urlArray.length - 1 ? urlArray[index + 1] : undefined;

    scenes.push({
      prompt: item.video_prompt,
      start_image,
      end_image
    })
  })

return scenes.map(scene => ({
  json: scene
}))
